// Problem:
//
// Implement wildcard pattern matching with support for '?' and '*'.
//
// '?' Matches any single character.
// '*' Matches any sequence of characters (including the empty sequence).
//
// The matching should cover the entire input string (not partial).
//
// The function prototype should be:
// bool isMatch(const char *s, const char *p)
//
// Some examples:
// isMatch("aa","a") → false
// isMatch("aa","aa") → true
// isMatch("aaa","aa") → false
// isMatch("aa", "*") → true
// isMatch("aa", "a*") → true
// isMatch("ab", "?*") → true
// isMatch("aab", "c*a*b") → false
//
#include <iostream>
#include <vector>
#include <string>

using namespace std;

class Solution {
public:
    bool isMatch(const string& s, const string& p) {
        return dfs(s, p, 0, 0) == 0;
    }

private:
    // return value:
    // 0: matched
    // 1: unmatched without reaching the end of s
    // 2: reach the end of s but unmatched
    int dfs(const string& s, const string& p, int si, int pi) {
        if (si == s.size() && pi == p.size()) return 0;
        if (si == s.size() && p[pi] != '*') return 1;
        if (pi == p.size()) return 2;

        if (p[pi] == '*') {
            if (pi + 1 < p.size() && p[pi + 1] == '*') {
                return dfs(s, p, si, pi + 1);
            }

            for (int i = 0; i + si <= s.size(); ++i) {
                int res = dfs(s, p, si + i, pi + 1);
                if (res == 0 || res == 1) {
                    return res;
                }
            }
        } else if (p[pi] == '?' || p[pi] == s[si]) {
            return dfs(s, p, si + 1, pi + 1);
        }
        return 2;
    }
};

class Solution_DP { // slower than dfs
public:
    bool isMatch(const string& s, const string& p) {
        bool dp[s.size() + 1];
        memset(dp, false, sizeof(dp));

        dp[0] = true;
        int i;
        for (int j = 0; j < p.size(); ++j) {
            if (p[j] == '*') {
                for (i = 1; i <= s.size(); ++i) {
                    dp[i] = dp[i] || dp[i - 1];
                }
            } else {
                if (p[j] == '?') {
                    for (i = s.size(); i >= 1; --i) {
                        dp[i] = dp[i - 1];
                    }
                } else {
                    for (i = s.size(); i >= 1; --i) {
                        dp[i] = dp[i - 1] && p[j] == s[i - 1];
                    }
                }
                dp[0] = false;
            }
        }
        return dp[s.size()];
    }
};

int main() {
    Solution sol;
    //Solution_DP sol;

    cout << sol.isMatch("aa", "*") << true << endl;
    cout << sol.isMatch("ho", "**ho") << true << endl;
    cout << sol.isMatch("", "*") << true << endl;
    cout << sol.isMatch("a", "a*") << true << endl;
    cout << sol.isMatch("aa", "a") << false << endl;
    cout << sol.isMatch("aa", "aa") << true << endl;
    cout << sol.isMatch("aaa", "aa") << false << endl;
    cout << sol.isMatch("aa", "*") << true << endl;
    cout << sol.isMatch("aa", "a*") << true << endl;
    cout << sol.isMatch("ab", "?*") << true << endl;
    cout << sol.isMatch("aab", "c*a*b") << false << endl;
    cout << sol.isMatch("", "**") << false << endl;

    return 0;
}
